//\uc815\ubcf4-\uae30\ubcf8\uc815\ubcf4(\uc9c1\uc804\uc8fc\ubb38)
boolean worldBlock = "eximbay".equals(siteinfo.s("pg_nm"));
DataSet loinfo = order.find("site_id = " + siteId + " AND user_id = " + userId + " AND status > 0", "*", "id DESC");
