  `ETC2` varchar(2000) DEFAULT NULL COMMENT '기타2',
  `ETC3` varchar(2000) DEFAULT NULL COMMENT '기타3',
  `ETC4` varchar(2000) DEFAULT NULL COMMENT '기타4',
